USE Lp_Account
GO

DECLARE @AllowedAccountNumberTable Table(AccountNumber varchar(50),Comment varchar(max))
DECLARE AccountToUpdate_Cursor CURSOR FOR 
SELECT AccountNumber,Comment
FROM @AllowedAccountNumberTable;
declare @commentDate DateTime
declare @userName nchar(100)
declare @processId varchar(50)
declare @commentToUpdate varchar(max)
declare @accountId  char(12)
declare @accountNumber varchar(50)
set @commentDate = GETDATE();
set @userName = 'SYSTEM';
set @processId = 'LETTER';



OPEN AccountToUpdate_Cursor
FETCH NEXT FROM AccountToUpdate_Cursor 
INTO @accountNumber,@commentToUpdate 

WHILE @@FETCH_STATUS = 0
BEGIN
    set @accountId=NULL;
    Select top 1 @accountId  = ISNULL(AccountIdLegacy,'NULL') from libertypower..Account (NOLOCK) where AccountNumber=@accountNumber
	   IF EXISTS (Select 1 from libertypower..Account (NOLOCK) where AccountIdLegacy=@accountId)
		  BEGIN
			 IF EXISTS(Select 1 from account_comments (nolock) where account_id=@accountId and comment=@commentToUpdate )
				BEGIN
				    Print 'Account Already Updated. Account Number ' +@accountNumber 
				END
			 ELSE
				BEGIN
				    Print 'Updating Account Number ' +@accountNumber 		
				    exec usp_AccountCommentInsert @AccountId=@accountId,@ProcessID=@processId,
				    @Comment=@commentToUpdate ,@Username=@userName,@CommentDate =@commentDate;
				END
		  END
	   ELSE
	   BEGIN
		  Print 'Unable to find Account id '+@accountNumber
	   END
	FETCH NEXT FROM AccountToUpdate_Cursor 
	INTO @accountNumber,@commentToUpdate 

END
CLOSE AccountToUpdate_Cursor;
DEALLOCATE AccountToUpdate_Cursor;


select act.AccountNumber
,acmts.*
,a.AccountIdLegacy from @AllowedAccountNumberTable act  
LEFT Join libertypower..Account a (nolock) on a.AccountNumber=act.AccountNumber
JOIN lp_account..Account_Comments acmts (nolock) on acmts.account_id=a.AccountIdLegacy
where acmts.process_id='LETTER' 
and acmts.comment = 'CT PURA Form 1 Quarterly Notice sent-May 15, 2015'

